import { createStore } from 'vuex'
import axios from 'axios'

const API_URL = 'http://localhost:8081'

export default createStore({
  state: {
    auth: {
      isLoggedIn: false
    },
    agendamentos: [],
    pagination: {
      currentPage: 0,
      totalPages: 0,
      totalElements: 0
    }
  },
  mutations: {
    'auth/login'(state) {
      state.auth.isLoggedIn = true
    },
    'auth/logout'(state) {
      state.auth.isLoggedIn = false
    },
    'agendamentos/setAgendamentos'(state, agendamentos) {
      state.agendamentos = agendamentos
    },
    'agendamentos/setPagination'(state, pagination) {
      state.pagination = pagination
    }
  },
  actions: {
    async login({ commit }, credentials) {
      // Simulação de login
      if (credentials.username && credentials.password) {
        commit('auth/login')
        return true
      }
      return false
    },
    async fetchAgendamentos({ commit }, { page = 0, size = 10 } = {}) {
      try {
        const response = await axios.get(`${API_URL}/agendamentos`, {
          params: { page, size }
        })
        
        if (response.data.content) {
          commit('agendamentos/setAgendamentos', response.data.content)
          commit('agendamentos/setPagination', {
            currentPage: response.data.number,
            totalPages: response.data.totalPages,
            totalElements: response.data.totalElements
          })
        } else {
          commit('agendamentos/setAgendamentos', response.data)
        }
      } catch (error) {
        console.error('Erro ao buscar agendamentos:', error)
        throw error
      }
    },
    async criarAgendamento(_, agendamento) {
      try {
        const response = await axios.post(`${API_URL}/agendamentos`, agendamento)
        return response.data
      } catch (error) {
        console.error('Erro ao criar agendamento:', error)
        throw error
      }
    },
    async excluirAgendamento(_, id) {
      try {
        await axios.delete(`${API_URL}/agendamentos/${id}`)
      } catch (error) {
        console.error('Erro ao excluir agendamento:', error)
        throw error
      }
    }
  }
})
