import { createApp } from 'vue'
import App from './App.vue'
import router from './router'
import store from './store'
import axios from 'axios'

// Configuração global do Axios
axios.defaults.baseURL = process.env.VUE_APP_API_URL || '/transfer'
axios.defaults.headers.common['Content-Type'] = 'application/json'
axios.defaults.headers.common['Accept'] = 'application/json'

// Interceptor para adicionar o token de autenticação
axios.interceptors.request.use(
  config => {
    const contaOrigem = store.state.auth.contaOrigem
    if (contaOrigem) {
      config.headers['X-Conta-Origem'] = contaOrigem
    }
    return config
  },
  error => {
    return Promise.reject(error)
  }
)

// Interceptor para tratamento de erros
axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response) {
      // O servidor respondeu com um status de erro
      const { data, status } = error.response
      store.commit('error/set', {
        message: data.message || 'Ocorreu um erro na operação',
        status,
        timestamp: data.timestamp,
        path: data.path
      })
    } else if (error.request) {
      // A requisição foi feita mas não houve resposta
      store.commit('error/set', {
        message: 'Não foi possível conectar ao servidor',
        status: 0
      })
    } else {
      // Erro na configuração da requisição
      store.commit('error/set', {
        message: 'Erro na configuração da requisição',
        status: 0
      })
    }
    return Promise.reject(error)
  }
)

const app = createApp(App)

app.use(store)
app.use(router)

app.mount('#app')
